codeExamples.code:
fn main() {
    let some_number = Some(5);
    let some_string = Some("a string");
    let absent_number: Option<i32> = None;

    println!("some_number: {:?}", some_number);
    println!("absent_number: {:?}", absent_number);
}

fn main() {
    let some_value = Some(42);
    let none_value: Option<i32> = None;

    // unwrap_or - provides default value if None
    let x = some_value.unwrap_or(0);  // 42
    let y = none_value.unwrap_or(0);  // 0

    println!("x: {}, y: {}", x, y);

    // expect - with error message
    let z = some_value.expect("Cannot be None");  // 42
    println!("z: {}", z);
}

fn divide(numerator: f64, denominator: f64) -> Option<f64> {
    if denominator != 0.0 {
        Some(numerator / denominator)
    } else {
        None
    }
}

fn main() {
    let result = divide(10.0, 2.0);
    match result {
        Some(value) => println!("Result: {}", value),
        None => println!("Division by zero!"),
    }

    let invalid = divide(10.0, 0.0);
    match invalid {
        Some(value) => println!("Result: {}", value),
        None => println!("Division by zero!"),
    }
}

exercises.starterCode:
// Write a find_number function that searches for a number in an array
// If found, return Some(value)
// If not, return None

fn main() {
    let numbers = vec![1, 2, 3, 4, 5];
    let result = find_number(&numbers, 3);
    println!("{:?}", result);
}

exercises.solution:
fn find_number(numbers: &[i32], target: i32) -> Option<i32> {
    for &num in numbers {
        if num == target {
            return Some(num);
        }
    }
    None
}

fn main() {
    let numbers = vec![1, 2, 3, 4, 5];
    let result = find_number(&numbers, 3);
    println!("{:?}", result);
}
