use std::thread;
use std::time::Duration;

fn main() {
    thread::spawn(|| {
        for i in 1..10 {
            println!("counter: {} from spawn thread", i);
            thread::sleep(Duration::from_millis(1));
        }
    });

    for i in 1..5 {
        println!("counter: {} from main thread", i);
        thread::sleep(Duration::from_millis(1));
    }
}

use std::thread;
use std::time::Duration;

fn main() {
    let handle = thread::spawn(|| {
        for i in 1..10 {
            println!("counter: {} from spawn thread", i);
            thread::sleep(Duration::from_millis(1));
        }
    });

    for i in 1..5 {
        println!("counter: {} from main thread", i);
        thread::sleep(Duration::from_millis(1));
    }

    handle.join().unwrap();  // Wait for thread to finish
}

use std::thread;

fn main() {
    let v = vec![1, 2, 3];

    let handle = thread::spawn(move || {
        println!("Here's a vector: {:?}", v);
    });

    // v is no longer usable here, because it was moved

    handle.join().unwrap();
}

use std::thread;

struct Block {
    data: String,
    nonce: u64,
    hash: String,
}

fn mine_block(data: String, start_nonce: u64, range: u64) -> Option<Block> {
    // Simplified mining: find nonce that makes hash start with "00"
    for nonce in start_nonce..start_nonce + range {
        let hash = format!("hash_{}_{}", data, nonce);
        if hash.starts_with("00") {
            return Some(Block { data, nonce, hash });
        }
    }
    None
}

fn main() {
    let data = String::from("block_data");

    // Mine in parallel on multiple threads
    let handle1 = thread::spawn(move || {
        mine_block(data.clone(), 0, 1000)
    });

    let handle2 = thread::spawn(move || {
        mine_block(data.clone(), 1000, 1000)
    });

    // Wait for first result
    if let Some(block) = handle1.join().unwrap() {
        println!("Mined block! Nonce: {}, Hash: {}", block.nonce, block.hash);
    } else if let Some(block) = handle2.join().unwrap() {
        println!("Mined block! Nonce: {}, Hash: {}", block.nonce, block.hash);
    }

    println!("Parallel mining allows faster block discovery!");
}

