codeExamples.code:
struct Block {
    index: u64,
    timestamp: u64,
    data: String,
    previous_hash: String,
    hash: String,
}

struct Blockchain {
    blocks: Vec<Block>,
}

impl Blockchain {
    fn new() -> Self {
        Blockchain {
            blocks: Vec::new(),
        }
    }

    fn add_block(&mut self, block: Block) {
        self.blocks.push(block);
    }
}

fn main() {
    let mut blockchain = Blockchain::new();

    let genesis = Block {
        index: 0,
        timestamp: 1234567890,
        data: String::from("Genesis Block"),
        previous_hash: String::from("0"),
        hash: String::from("genesis_hash"),
    };

    blockchain.add_block(genesis);
    println!("Blockchain created with {} blocks", blockchain.blocks.len());
}

struct Block {
    index: u64,
    previous_hash: String,
    hash: String,
}

fn is_valid_block(current: &Block, previous: &Block) -> bool {
    // Check if index is sequential
    if current.index != previous.index + 1 {
        return false;
    }

    // Check if previous hash matches
    if current.previous_hash != previous.hash {
        return false;
    }

    true
}

fn main() {
    let block1 = Block {
        index: 0,
        previous_hash: String::from("0"),
        hash: String::from("hash1"),
    };

    let block2 = Block {
        index: 1,
        previous_hash: String::from("hash1"),
        hash: String::from("hash2"),
    };

    println!("Block valid: {}", is_valid_block(&block2, &block1));
}

