exercises.solution:
struct Block {
    index: u64,
    data: String,
    previous_hash: String,
    hash: String,
}

struct Blockchain {
    blocks: Vec<Block>,
}

fn main() {
    let mut blockchain = Blockchain {
        blocks: Vec::new(),
    };

    let genesis = Block {
        index: 0,
        data: String::from("Genesis"),
        previous_hash: String::from("0"),
        hash: String::from("genesis_hash"),
    };

    blockchain.blocks.push(genesis);

    let block1 = Block {
        index: 1,
        data: String::from("Block 1"),
        previous_hash: String::from("genesis_hash"),
        hash: String::from("block1_hash"),
    };

    blockchain.blocks.push(block1);

    println!("Blockchain has {} blocks", blockchain.blocks.len());
}
