fn main() {
    let s = String::from("hello");
    println!("s value: {}", s);
    {
        let inner = String::from("world");
        println!("inner value: {}", inner);
    }
    println!("s is still valid: {}", s);
}

fn main() {
    let s1 = String::from("hello");
    let s2 = s1;
    println!("s2 value: {}", s2);
}

fn main() {
    let x = 5;
    let y = x;
    println!("x: {}", x);
    println!("y: {}", y);
    let tup = (1, 2, 3);
    let tup2 = tup;
    println!("tup: {:?}", tup);
}

struct Transaction {
    from: String,
    to: String,
    amount: u64,
}
fn process_transaction(tx: Transaction) {
    println!("Processing: {} -> {} ({} tokens)", tx.from, tx.to, tx.amount);
}
fn main() {
    let tx = Transaction {
        from: String::from("0xAlice"),
        to: String::from("0xBob"),
        amount: 100,
    };
    process_transaction(tx);
}

